import React, { useState, useEffect, useRef } from 'react';
import Editor from '@monaco-editor/react';
import { FaUser, FaHistory, FaTrash, FaMoon, FaSun, FaPlay, FaSpinner, FaBars, FaTimes, FaChevronLeft, FaChevronRight, FaGripLines } from 'react-icons/fa';
import { toast } from 'react-toastify';

function Debugger() {
  const [code, setCode] = useState('// Write your code here\nfunction example() {\n  console.log("Hello World");\n}');
  const [language, setLanguage] = useState('javascript');
  const [darkMode, setDarkMode] = useState(true);
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [history, setHistory] = useState([]);
  const [isSidebarOpen, setSidebarOpen] = useState(false);
  const [isResultsPanelOpen, setResultsPanelOpen] = useState(false);
  const [editorWidth, setEditorWidth] = useState(60); // Default editor width 60%
  const [resultsWidth, setResultsWidth] = useState(35); // Default results width 35%
  const [screenSize, setScreenSize] = useState('lg'); // Track screen size
  const editorRef = useRef(null);
  const resizeTimeoutRef = useRef(null);
  const isResizingEditorRef = useRef(false);
  const isResizingResultsRef = useRef(false);
  const startXRef = useRef(0);
  const startWidthsRef = useRef({ editor: 60, results: 35 });

  // Backend URL configuration
  const BACKEND_URL = 'http://localhost:5000';

  // Screen size breakpoints
  const breakpoints = {
    sm: 640,
    md: 768,
    lg: 1024,
    xl: 1280,
    '2xl': 1536
  };

  // Get width constraints based on screen size
  const getWidthConstraints = (size) => {
    switch (size) {
      case 'sm':
        return { editor: { min: 70, max: 100 }, results: { min: 70, max: 100 } };
      case 'md':
        return { editor: { min: 60, max: 90 }, results: { min: 40, max: 80 } };
      case 'lg':
        return { editor: { min: 40, max: 75 }, results: { min: 25, max: 55 } };
      case 'xl':
      case '2xl':
        return { editor: { min: 30, max: 70 }, results: { min: 20, max: 50 } };
      default:
        return { editor: { min: 40, max: 75 }, results: { min: 25, max: 55 } };
    }
  };

  // Update screen size
  useEffect(() => {
    const handleResize = () => {
      const width = window.innerWidth;
      let newSize = '2xl';
      
      if (width < breakpoints.sm) newSize = 'sm';
      else if (width < breakpoints.md) newSize = 'md';
      else if (width < breakpoints.lg) newSize = 'lg';
      else if (width < breakpoints.xl) newSize = 'xl';

      setScreenSize(newSize);

      // Adjust panel widths based on new screen size
      const constraints = getWidthConstraints(newSize);
      if (width < breakpoints.lg) {
        // Mobile/tablet view - full width
        setEditorWidth(100);
        setResultsWidth(100);
      } else {
        // Desktop view - maintain proportions within constraints
        setEditorWidth(prev => Math.min(Math.max(prev, constraints.editor.min), constraints.editor.max));
        setResultsWidth(prev => Math.min(Math.max(prev, constraints.results.min), constraints.results.max));
      }
    };

    handleResize(); // Initial check
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Handle editor mounting
  const handleEditorDidMount = (editor, monaco) => {
    editorRef.current = editor;
  };

  // Handle resize start for both panels
  const handleResizeStart = (e, panel) => {
    e.preventDefault();
    if (panel === 'editor') {
      isResizingEditorRef.current = true;
    } else {
      isResizingResultsRef.current = true;
    }
    startXRef.current = e.clientX;
    startWidthsRef.current = { editor: editorWidth, results: resultsWidth };
    
    document.addEventListener('mousemove', handleResizeMove);
    document.addEventListener('mouseup', handleResizeEnd);
    document.body.classList.add('select-none');
  };

  // Handle resize move for both panels
  const handleResizeMove = (e) => {
    if (!isResizingEditorRef.current && !isResizingResultsRef.current) return;

    const container = document.getElementById('editor-container');
    if (!container) return;

    const containerWidth = container.getBoundingClientRect().width;
    const deltaX = e.clientX - startXRef.current;
    const deltaPercent = (deltaX / containerWidth) * 100;
    const constraints = getWidthConstraints(screenSize);

    if (isResizingEditorRef.current) {
      const newEditorWidth = startWidthsRef.current.editor + deltaPercent;
      setEditorWidth(Math.min(Math.max(newEditorWidth, constraints.editor.min), constraints.editor.max));
    } else if (isResizingResultsRef.current) {
      const newResultsWidth = startWidthsRef.current.results - deltaPercent;
      setResultsWidth(Math.min(Math.max(newResultsWidth, constraints.results.min), constraints.results.max));
    }

    if (editorRef.current) {
      editorRef.current.layout();
    }
  };

  // Handle resize end
  const handleResizeEnd = () => {
    isResizingEditorRef.current = false;
    isResizingResultsRef.current = false;
    document.removeEventListener('mousemove', handleResizeMove);
    document.removeEventListener('mouseup', handleResizeEnd);
    document.body.classList.remove('select-none');
    handleResize();
  };

  // Debounced resize handler
  const handleResize = () => {
    if (resizeTimeoutRef.current) {
      clearTimeout(resizeTimeoutRef.current);
    }
    resizeTimeoutRef.current = setTimeout(() => {
      if (editorRef.current) {
        editorRef.current.layout();
      }
    }, 100);
  };

  // Clean up resize observer
  useEffect(() => {
    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
      if (resizeTimeoutRef.current) {
        clearTimeout(resizeTimeoutRef.current);
      }
    };
  }, []);

  // Handle layout changes
  useEffect(() => {
    const handleLayoutChange = () => {
      if (window.innerWidth >= 1024) {
        setSidebarOpen(false);
        setResultsPanelOpen(false);
      }
      handleResize();
    };

    window.addEventListener('resize', handleLayoutChange);
    return () => window.removeEventListener('resize', handleLayoutChange);
  }, []);

  // Update editor layout when any panel width changes
  useEffect(() => {
    handleResize();
  }, [isSidebarOpen, isResultsPanelOpen, editorWidth, resultsWidth]);

  const handleEditorChange = (value) => {
    setCode(value || '');
  };

  const handleLanguageChange = (e) => {
    setLanguage(e.target.value);
    // Reset code based on language
    const defaultCodes = {
      javascript: '// Write your JavaScript code here\nfunction example() {\n  console.log("Hello World");\n}',
      python: '# Write your Python code here\ndef example():\n    print("Hello World")',
      java: '// Write your Java code here\npublic class Main {\n    public static void main(String[] args) {\n        System.out.println("Hello World");\n    }\n}',
      cpp: '// Write your C++ code here\n#include <iostream>\n\nint main() {\n    std::cout << "Hello World" << std::endl;\n    return 0;\n}'
    };
    setCode(defaultCodes[e.target.value] || defaultCodes.javascript);
  };

  const handleAnalyze = async () => {
    if (!code.trim()) {
      toast.error('Please write some code first');
      return;
    }

    setLoading(true);
    try {
      const response = await fetch(`${BACKEND_URL}/api/debug`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ code, language }),
      });

      if (!response.ok) {
        throw new Error('Failed to analyze code');
      }

      const data = await response.json();
      setAnalysis(data);
      setResultsPanelOpen(true); // Open results panel on mobile after analysis
      
      // Add to history
      setHistory(prev => [{
        id: Date.now(),
        code,
        language,
        analysis: data,
        timestamp: new Date().toLocaleString()
      }, ...prev]);

      toast.success('Analysis completed successfully');
    } catch (error) {
      console.error('Error analyzing code:', error);
      toast.error('Failed to analyze code. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleClearHistory = () => {
    setHistory([]);
    toast.success('History cleared');
  };

  const handleLoadFromHistory = (item) => {
    setCode(item.code);
    setLanguage(item.language);
    setAnalysis(item.analysis);
  };

  return (
    <div className="relative flex flex-col lg:flex-row h-screen bg-gray-900">
      {/* Mobile Header */}
      <div className="lg:hidden flex items-center justify-between p-4 bg-gray-800/95 backdrop-blur-sm border-b border-gray-700 sticky top-0 z-50">
        <button
          onClick={() => setSidebarOpen(!isSidebarOpen)}
          className="text-gray-300 hover:text-white p-2 rounded-lg hover:bg-gray-700/50"
        >
          {isSidebarOpen ? <FaTimes /> : <FaBars />}
        </button>
        <select
          value={language}
          onChange={handleLanguageChange}
          className="bg-gray-700/50 text-white px-3 py-2 rounded-lg text-sm border border-gray-600 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
        >
          <option value="javascript">JavaScript</option>
          <option value="python">Python</option>
          <option value="java">Java</option>
          <option value="cpp">C++</option>
        </select>
        <button
          onClick={() => setResultsPanelOpen(!isResultsPanelOpen)}
          className="text-gray-300 hover:text-white p-2 rounded-lg hover:bg-gray-700/50"
        >
          <FaChevronLeft className={`transform transition-transform ${isResultsPanelOpen ? 'rotate-180' : ''}`} />
        </button>
      </div>

      {/* Sidebar */}
      <div className={`
        fixed lg:static inset-y-0 left-0 z-40
        w-72 bg-gray-800/95 backdrop-blur-sm border-r border-gray-700
        transform transition-transform duration-300 ease-in-out
        ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'}
        lg:translate-x-0
        flex flex-col
      `}>
        <div className="flex flex-col h-full">
          {/* Close button for mobile */}
          <div className="lg:hidden flex justify-end p-4">
            <button
              onClick={() => setSidebarOpen(false)}
              className="text-gray-300 hover:text-white p-2 rounded-lg hover:bg-gray-700/50"
            >
              <FaTimes />
            </button>
          </div>

          {/* User Info */}
          <div className="flex items-center p-4 border-b border-gray-700/50">
            <div className="w-10 h-10 rounded-full bg-gradient-to-br from-blue-500 to-purple-600 flex items-center justify-center">
              <FaUser className="text-white" />
            </div>
            <span className="ml-3 text-gray-200 font-medium">User</span>
          </div>

          {/* History */}
          <div className="flex-1 p-4 overflow-hidden">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-gray-300 font-semibold">History</h3>
              {history.length > 0 && (
                <button
                  onClick={handleClearHistory}
                  className="text-gray-400 hover:text-red-400 p-1 rounded hover:bg-gray-700/50"
                >
                  <FaTrash />
                </button>
              )}
            </div>
            <div className="space-y-2 h-full overflow-y-auto custom-scrollbar">
              {history.length > 0 ? (
                history.map(item => (
                  <div
                    key={item.id}
                    onClick={() => {
                      handleLoadFromHistory(item);
                      setSidebarOpen(false);
                    }}
                    className="p-3 bg-gray-700/30 rounded-lg cursor-pointer hover:bg-gray-700/50 transition-colors duration-150"
                  >
                    <div className="text-sm text-gray-200">{item.language}</div>
                    <div className="text-xs text-gray-400">{item.timestamp}</div>
                  </div>
                ))
              ) : (
                <div className="text-gray-400 text-sm text-center py-4">No debugging sessions yet</div>
              )}
            </div>
          </div>

          {/* Theme Toggle */}
          <div className="p-4 border-t border-gray-700/50">
            <button
              className="flex items-center justify-center w-full text-gray-300 hover:text-white p-2 rounded-lg hover:bg-gray-700/50 transition-colors duration-150"
              onClick={() => setDarkMode(!darkMode)}
            >
              {darkMode ? <FaSun className="mr-2" /> : <FaMoon className="mr-2" />}
              {darkMode ? 'Light Mode' : 'Dark Mode'}
            </button>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div id="editor-container" className="flex-1 flex flex-col lg:flex-row h-full lg:h-screen relative">
        {/* Editor Section */}
        <div 
          className={`
            flex-1 flex flex-col h-full
            transition-all duration-300 ease-in-out
            ${screenSize === 'sm' || screenSize === 'md' ? 'w-full' : ''}
          `}
          style={{ width: `${editorWidth}%` }}
        >
          {/* Desktop Controls */}
          <div className="hidden lg:flex justify-between items-center p-4 bg-gray-800/95 backdrop-blur-sm border-b border-gray-700">
            <select
              value={language}
              onChange={handleLanguageChange}
              className="bg-gray-700/50 text-white px-4 py-2 rounded-lg text-sm border border-gray-600 focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
            >
              <option value="javascript">JavaScript</option>
              <option value="python">Python</option>
              <option value="java">Java</option>
              <option value="cpp">C++</option>
            </select>
            <button
              onClick={handleAnalyze}
              disabled={loading}
              className="bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 text-white px-6 py-2 rounded-lg flex items-center space-x-2 transition-colors duration-150 disabled:opacity-50"
            >
              {loading ? (
                <>
                  <FaSpinner className="animate-spin" />
                  <span>Analyzing...</span>
                </>
              ) : (
                <>
                  <FaPlay />
                  <span>Analyze Code</span>
                </>
              )}
            </button>
          </div>

          {/* Editor */}
          <div className="flex-1 p-4">
            <div className="h-full rounded-lg overflow-hidden border border-gray-700/50 shadow-lg">
              <Editor
                height="100%"
                defaultLanguage={language}
                value={code}
                onChange={handleEditorChange}
                onMount={handleEditorDidMount}
                theme={darkMode ? 'vs-dark' : 'light'}
                options={{
                  minimap: { enabled: true },
                  fontSize: 14,
                  lineNumbers: 'on',
                  roundedSelection: false,
                  scrollBeyondLastLine: false,
                  readOnly: false,
                  automaticLayout: false,
                  wordWrap: 'on',
                  formatOnPaste: true,
                  formatOnType: true,
                  suggestOnTriggerCharacters: true,
                  tabSize: 2,
                  padding: { top: 16, bottom: 16 },
                }}
                className="w-full h-full"
              />
            </div>
          </div>

          {/* Mobile Analyze Button */}
          <div className="lg:hidden p-4 bg-gray-800/95 backdrop-blur-sm border-t border-gray-700 sticky bottom-0">
            <button
              onClick={handleAnalyze}
              disabled={loading}
              className="w-full bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 text-white px-4 py-3 rounded-lg flex items-center justify-center space-x-2 transition-colors duration-150 disabled:opacity-50"
            >
              {loading ? (
                <>
                  <FaSpinner className="animate-spin" />
                  <span>Analyzing...</span>
                </>
              ) : (
                <>
                  <FaPlay />
                  <span>Analyze Code</span>
                </>
              )}
            </button>
          </div>
        </div>

        {/* Editor Resize Handle - Only show on larger screens */}
        {screenSize !== 'sm' && screenSize !== 'md' && (
          <div
            className="hidden lg:flex items-center justify-center w-1 hover:w-2 cursor-col-resize group transition-all duration-150 z-10"
            onMouseDown={(e) => handleResizeStart(e, 'editor')}
          >
            <div className="w-0.5 h-full bg-gray-700 group-hover:bg-blue-500 transition-colors duration-150" />
          </div>
        )}

        {/* Analysis Results Panel */}
        <div className={`
          fixed lg:static inset-y-0 right-0 z-30
          ${screenSize === 'sm' || screenSize === 'md' ? 'w-full' : `lg:w-[${resultsWidth}%]`}
          bg-gray-800/95 backdrop-blur-sm border-l border-gray-700
          transform transition-all duration-300 ease-in-out
          ${isResultsPanelOpen ? 'translate-x-0' : 'translate-x-full'}
          lg:translate-x-0
          overflow-y-auto
          relative
        `}>
          {/* Results Panel Resize Handle - Only show on larger screens */}
          {screenSize !== 'sm' && screenSize !== 'md' && (
            <div
              className="hidden lg:flex items-center justify-center absolute left-0 top-0 bottom-0 w-1 hover:w-2 cursor-col-resize group transition-all duration-150 z-10"
              onMouseDown={(e) => handleResizeStart(e, 'results')}
            >
              <div className="w-0.5 h-full bg-gray-700 group-hover:bg-blue-500 transition-colors duration-150" />
            </div>
          )}

          <div className="lg:hidden flex justify-between items-center p-4 border-b border-gray-700 sticky top-0 bg-gray-800/95 backdrop-blur-sm">
            <h2 className="text-lg font-bold text-gray-200">Analysis Results</h2>
            <button
              onClick={() => setResultsPanelOpen(false)}
              className="text-gray-300 hover:text-white p-2 rounded-lg hover:bg-gray-700/50"
            >
              <FaTimes />
            </button>
          </div>

          <div className="p-4">
            <h2 className="hidden lg:block text-xl font-bold mb-6 text-gray-200">Analysis Results</h2>
            {analysis ? (
              <div className="space-y-6">
                {analysis.issues && analysis.issues.length > 0 && (
                  <div>
                    <h3 className="text-lg font-semibold mb-3 text-red-400">Detected Issues</h3>
                    <ul className="space-y-3">
                      {analysis.issues.map((issue, index) => (
                        <li key={index} className="bg-red-900/20 border border-red-900/30 rounded-lg p-4">
                          <div className="font-medium text-red-400">Line {issue.lineNumber}:</div>
                          <div className="text-sm text-red-300 mt-1">{issue.message}</div>
                          {issue.suggestion && (
                            <div className="text-sm text-gray-400 mt-2 p-2 bg-gray-700/30 rounded">
                              Suggestion: {issue.suggestion}
                            </div>
                          )}
                        </li>
                      ))}
                    </ul>
                  </div>
                )}

                {analysis.suggestions && analysis.suggestions.length > 0 && (
                  <div>
                    <h3 className="text-lg font-semibold mb-3 text-green-400">Suggestions</h3>
                    <ul className="space-y-3">
                      {analysis.suggestions.map((suggestion, index) => (
                        <li key={index} className="bg-green-900/20 border border-green-900/30 rounded-lg p-4 text-green-300">
                          {suggestion}
                        </li>
                      ))}
                    </ul>
                  </div>
                )}

                {analysis.optimizedCode && (
                  <div>
                    <h3 className="text-lg font-semibold mb-3 text-blue-400">Optimized Code</h3>
                    <pre className="bg-gray-700/30 border border-gray-700 p-4 rounded-lg text-sm overflow-x-auto text-blue-300">
                      {analysis.optimizedCode}
                    </pre>
                  </div>
                )}
              </div>
            ) : (
              <div className="flex flex-col items-center justify-center py-12 text-gray-400">
                {loading ? (
                  <div className="flex flex-col items-center space-y-4">
                    <FaSpinner className="animate-spin text-3xl text-blue-500" />
                    <p>Analyzing your code...</p>
                  </div>
                ) : (
                  <div className="text-center">
                    <p className="text-lg mb-2">No Analysis Results</p>
                    <p className="text-sm text-gray-500">Click "Analyze Code" to begin</p>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Overlay for mobile when sidebar is open */}
      {isSidebarOpen && (
        <div
          className="fixed inset-0 bg-black/50 backdrop-blur-sm z-30 lg:hidden"
          onClick={() => setSidebarOpen(false)}
        />
      )}
    </div>
  );
}

// Add custom scrollbar styles
const style = document.createElement('style');
style.textContent = `
  .custom-scrollbar::-webkit-scrollbar {
    width: 6px;
  }
  .custom-scrollbar::-webkit-scrollbar-track {
    background: rgba(31, 41, 55, 0.5);
    border-radius: 3px;
  }
  .custom-scrollbar::-webkit-scrollbar-thumb {
    background: rgba(75, 85, 99, 0.5);
    border-radius: 3px;
  }
  .custom-scrollbar::-webkit-scrollbar-thumb:hover {
    background: rgba(75, 85, 99, 0.7);
  }
`;
document.head.appendChild(style);

export default Debugger;
