import React, { useState } from 'react';
import Editor from '@monaco-editor/react';
import { FaUser, FaHistory, FaTrash, FaMoon, FaSun } from 'react-icons/fa';

function Debugger() {
  const [code, setCode] = useState('// Write your code here');
  const [language, setLanguage] = useState('javascript');
  const [darkMode, setDarkMode] = useState(true);
  const [analysis, setAnalysis] = useState(null);

  const handleEditorChange = (value) => {
    setCode(value);
  };

  const handleLanguageChange = (e) => {
    setLanguage(e.target.value);
  };

  const handleAnalyze = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/debug', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ code, language }),
      });
      const data = await response.json();
      setAnalysis(data);
    } catch (error) {
      console.error('Error analyzing code:', error);
    }
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <div className="w-64 bg-gray-800 p-4">
        <div className="flex items-center mb-8">
          <div className="w-10 h-10 rounded-full bg-gray-700 flex items-center justify-center">
            <FaUser className="text-gray-300" />
          </div>
          <span className="ml-3 text-gray-300">User</span>
        </div>

        <div className="mb-8">
          <h3 className="text-gray-400 mb-2">History</h3>
          <div className="space-y-2">
            {/* History items would go here */}
            <div className="text-gray-300">No debugging sessions yet</div>
          </div>
        </div>

        <button
          className="flex items-center text-gray-300 hover:text-white"
          onClick={() => setDarkMode(!darkMode)}
        >
          {darkMode ? <FaSun className="mr-2" /> : <FaMoon className="mr-2" />}
          {darkMode ? 'Light Mode' : 'Dark Mode'}
        </button>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        <div className="flex-1 flex">
          {/* Code Editor */}
          <div className="flex-1 p-4">
            <div className="flex justify-between items-center mb-4">
              <select
                value={language}
                onChange={handleLanguageChange}
                className="bg-gray-700 text-white px-3 py-2 rounded"
              >
                <option value="javascript">JavaScript</option>
                <option value="python">Python</option>
                <option value="java">Java</option>
                <option value="cpp">C++</option>
              </select>
              <button
                onClick={handleAnalyze}
                className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
              >
                Analyze Code
              </button>
            </div>
            <Editor
              height="80vh"
              defaultLanguage={language}
              value={code}
              onChange={handleEditorChange}
              theme={darkMode ? 'vs-dark' : 'light'}
              options={{
                minimap: { enabled: false },
                fontSize: 14,
              }}
            />
          </div>

          {/* Analysis Results */}
          <div className="w-1/3 bg-gray-800 p-4 overflow-y-auto">
            <h2 className="text-xl font-bold mb-4">Analysis Results</h2>
            {analysis ? (
              <div>
                <h3 className="text-lg font-semibold mb-2">Detected Issues</h3>
                <ul className="space-y-2">
                  {analysis.issues.map((issue, index) => (
                    <li key={index} className="text-red-400">
                      Line {issue.lineNumber}: {issue.message}
                    </li>
                  ))}
                </ul>

                <h3 className="text-lg font-semibold mt-4 mb-2">Suggestions</h3>
                <ul className="space-y-2">
                  {analysis.suggestions.map((suggestion, index) => (
                    <li key={index} className="text-green-400">
                      {suggestion}
                    </li>
                  ))}
                </ul>
              </div>
            ) : (
              <p className="text-gray-400">Run analysis to see results</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Debugger;
