import React, { useState, useEffect, useRef } from 'react';
import Editor from '@monaco-editor/react';
import { FaUser, FaHistory, FaTrash, FaMoon, FaSun, FaPlay, FaSpinner, FaBars, FaTimes, FaChevronLeft, FaChevronRight } from 'react-icons/fa';
import { toast } from 'react-toastify';

function Debugger() {
  const [code, setCode] = useState('// Write your code here\nfunction example() {\n  console.log("Hello World");\n}');
  const [language, setLanguage] = useState('javascript');
  const [darkMode, setDarkMode] = useState(true);
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [history, setHistory] = useState([]);
  const [isSidebarOpen, setSidebarOpen] = useState(false);
  const [isResultsPanelOpen, setResultsPanelOpen] = useState(false);
  const editorRef = useRef(null);
  const resizeTimeoutRef = useRef(null);

  // Backend URL configuration
  const BACKEND_URL = 'http://localhost:5000';

  // Handle editor mounting
  const handleEditorDidMount = (editor, monaco) => {
    editorRef.current = editor;
  };

  // Debounced resize handler
  const handleResize = () => {
    if (resizeTimeoutRef.current) {
      clearTimeout(resizeTimeoutRef.current);
    }
    resizeTimeoutRef.current = setTimeout(() => {
      if (editorRef.current) {
        editorRef.current.layout();
      }
    }, 100);
  };

  // Clean up resize observer
  useEffect(() => {
    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
      if (resizeTimeoutRef.current) {
        clearTimeout(resizeTimeoutRef.current);
      }
    };
  }, []);

  // Handle layout changes
  useEffect(() => {
    const handleLayoutChange = () => {
      if (window.innerWidth >= 1024) {
        setSidebarOpen(false);
        setResultsPanelOpen(false);
      }
      handleResize();
    };

    window.addEventListener('resize', handleLayoutChange);
    return () => window.removeEventListener('resize', handleLayoutChange);
  }, []);

  // Update editor layout when panels open/close
  useEffect(() => {
    handleResize();
  }, [isSidebarOpen, isResultsPanelOpen]);

  const handleEditorChange = (value) => {
    setCode(value || '');
  };

  const handleLanguageChange = (e) => {
    setLanguage(e.target.value);
    // Reset code based on language
    const defaultCodes = {
      javascript: '// Write your JavaScript code here\nfunction example() {\n  console.log("Hello World");\n}',
      python: '# Write your Python code here\ndef example():\n    print("Hello World")',
      java: '// Write your Java code here\npublic class Main {\n    public static void main(String[] args) {\n        System.out.println("Hello World");\n    }\n}',
      cpp: '// Write your C++ code here\n#include <iostream>\n\nint main() {\n    std::cout << "Hello World" << std::endl;\n    return 0;\n}'
    };
    setCode(defaultCodes[e.target.value] || defaultCodes.javascript);
  };

  const handleAnalyze = async () => {
    if (!code.trim()) {
      toast.error('Please write some code first');
      return;
    }

    setLoading(true);
    try {
      const response = await fetch(`${BACKEND_URL}/api/debug`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ code, language }),
      });

      if (!response.ok) {
        throw new Error('Failed to analyze code');
      }

      const data = await response.json();
      setAnalysis(data);
      setResultsPanelOpen(true); // Open results panel on mobile after analysis
      
      // Add to history
      setHistory(prev => [{
        id: Date.now(),
        code,
        language,
        analysis: data,
        timestamp: new Date().toLocaleString()
      }, ...prev]);

      toast.success('Analysis completed successfully');
    } catch (error) {
      console.error('Error analyzing code:', error);
      toast.error('Failed to analyze code. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleClearHistory = () => {
    setHistory([]);
    toast.success('History cleared');
  };

  const handleLoadFromHistory = (item) => {
    setCode(item.code);
    setLanguage(item.language);
    setAnalysis(item.analysis);
  };

  return (
    <div className="relative flex flex-col lg:flex-row h-screen bg-gray-900">
      {/* Mobile Header */}
      <div className="lg:hidden flex items-center justify-between p-4 bg-gray-800 border-b border-gray-700">
        <button
          onClick={() => setSidebarOpen(!isSidebarOpen)}
          className="text-gray-300 hover:text-white"
        >
          {isSidebarOpen ? <FaTimes /> : <FaBars />}
        </button>
        <select
          value={language}
          onChange={handleLanguageChange}
          className="bg-gray-700 text-white px-3 py-2 rounded text-sm"
        >
          <option value="javascript">JavaScript</option>
          <option value="python">Python</option>
          <option value="java">Java</option>
          <option value="cpp">C++</option>
        </select>
        <button
          onClick={() => setResultsPanelOpen(!isResultsPanelOpen)}
          className="text-gray-300 hover:text-white"
        >
          <FaChevronLeft className={`transform transition-transform ${isResultsPanelOpen ? 'rotate-180' : ''}`} />
        </button>
      </div>

      {/* Sidebar */}
      <div className={`
        fixed lg:static inset-y-0 left-0 z-30
        w-64 bg-gray-800 border-r border-gray-700
        transform transition-transform duration-300 ease-in-out
        ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'}
        lg:translate-x-0
      `}>
        <div className="flex flex-col h-full">
          {/* Close button for mobile */}
          <div className="lg:hidden flex justify-end p-4">
            <button
              onClick={() => setSidebarOpen(false)}
              className="text-gray-300 hover:text-white"
            >
              <FaTimes />
            </button>
          </div>

          {/* User Info */}
          <div className="flex items-center p-4">
            <div className="w-10 h-10 rounded-full bg-gray-700 flex items-center justify-center">
              <FaUser className="text-gray-300" />
            </div>
            <span className="ml-3 text-gray-300">User</span>
          </div>

          {/* History */}
          <div className="flex-1 p-4 overflow-hidden">
            <div className="flex justify-between items-center mb-2">
              <h3 className="text-gray-400">History</h3>
              {history.length > 0 && (
                <button
                  onClick={handleClearHistory}
                  className="text-gray-400 hover:text-red-400"
                >
                  <FaTrash />
                </button>
              )}
            </div>
            <div className="space-y-2 h-full overflow-y-auto">
              {history.length > 0 ? (
                history.map(item => (
                  <div
                    key={item.id}
                    onClick={() => {
                      handleLoadFromHistory(item);
                      setSidebarOpen(false);
                    }}
                    className="p-2 bg-gray-700 rounded cursor-pointer hover:bg-gray-600"
                  >
                    <div className="text-sm text-gray-300">{item.language}</div>
                    <div className="text-xs text-gray-400">{item.timestamp}</div>
                  </div>
                ))
              ) : (
                <div className="text-gray-400 text-sm">No debugging sessions yet</div>
              )}
            </div>
          </div>

          {/* Theme Toggle */}
          <div className="p-4 border-t border-gray-700">
            <button
              className="flex items-center text-gray-300 hover:text-white w-full"
              onClick={() => setDarkMode(!darkMode)}
            >
              {darkMode ? <FaSun className="mr-2" /> : <FaMoon className="mr-2" />}
              {darkMode ? 'Light Mode' : 'Dark Mode'}
            </button>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col h-full lg:h-screen">
        {/* Editor Section */}
        <div className="flex-1 flex flex-col">
          {/* Desktop Controls */}
          <div className="hidden lg:flex justify-between items-center p-4">
            <select
              value={language}
              onChange={handleLanguageChange}
              className="bg-gray-700 text-white px-3 py-2 rounded"
            >
              <option value="javascript">JavaScript</option>
              <option value="python">Python</option>
              <option value="java">Java</option>
              <option value="cpp">C++</option>
            </select>
            <button
              onClick={handleAnalyze}
              disabled={loading}
              className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded flex items-center"
            >
              {loading ? (
                <>
                  <FaSpinner className="animate-spin mr-2" />
                  Analyzing...
                </>
              ) : (
                <>
                  <FaPlay className="mr-2" />
                  Analyze Code
                </>
              )}
            </button>
          </div>

          {/* Editor */}
          <div className="flex-1 p-4">
            <div className="h-full border border-gray-700 rounded-lg overflow-hidden">
              <Editor
                height="100%"
                defaultLanguage={language}
                value={code}
                onChange={handleEditorChange}
                onMount={handleEditorDidMount}
                theme={darkMode ? 'vs-dark' : 'light'}
                options={{
                  minimap: { enabled: true },
                  fontSize: 14,
                  lineNumbers: 'on',
                  roundedSelection: false,
                  scrollBeyondLastLine: false,
                  readOnly: false,
                  automaticLayout: false,
                  wordWrap: 'on',
                  formatOnPaste: true,
                  formatOnType: true,
                  suggestOnTriggerCharacters: true,
                  tabSize: 2,
                }}
                className="w-full h-full"
              />
            </div>
          </div>

          {/* Mobile Analyze Button */}
          <div className="lg:hidden p-4">
            <button
              onClick={handleAnalyze}
              disabled={loading}
              className="w-full bg-blue-600 hover:bg-blue-700 text-white px-4 py-3 rounded flex items-center justify-center"
            >
              {loading ? (
                <>
                  <FaSpinner className="animate-spin mr-2" />
                  Analyzing...
                </>
              ) : (
                <>
                  <FaPlay className="mr-2" />
                  Analyze Code
                </>
              )}
            </button>
          </div>
        </div>

        {/* Analysis Results Panel */}
        <div className={`
          fixed lg:static inset-y-0 right-0 z-20
          w-full lg:w-1/3 bg-gray-800 border-l border-gray-700
          transform transition-transform duration-300 ease-in-out
          ${isResultsPanelOpen ? 'translate-x-0' : 'translate-x-full'}
          lg:translate-x-0
          overflow-y-auto
        `}>
          <div className="lg:hidden flex justify-between items-center p-4 border-b border-gray-700">
            <h2 className="text-lg font-bold">Analysis Results</h2>
            <button
              onClick={() => setResultsPanelOpen(false)}
              className="text-gray-300 hover:text-white"
            >
              <FaTimes />
            </button>
          </div>

          <div className="p-4">
            <h2 className="hidden lg:block text-xl font-bold mb-4">Analysis Results</h2>
            {analysis ? (
              <div className="space-y-6">
                {analysis.issues && analysis.issues.length > 0 && (
                  <div>
                    <h3 className="text-lg font-semibold mb-2 text-red-400">Detected Issues</h3>
                    <ul className="space-y-2">
                      {analysis.issues.map((issue, index) => (
                        <li key={index} className="text-red-400 bg-red-900/20 p-2 rounded">
                          <div className="font-medium">Line {issue.lineNumber}:</div>
                          <div className="text-sm">{issue.message}</div>
                          {issue.suggestion && (
                            <div className="text-sm text-gray-400 mt-1">
                              Suggestion: {issue.suggestion}
                            </div>
                          )}
                        </li>
                      ))}
                    </ul>
                  </div>
                )}

                {analysis.suggestions && analysis.suggestions.length > 0 && (
                  <div>
                    <h3 className="text-lg font-semibold mb-2 text-green-400">Suggestions</h3>
                    <ul className="space-y-2">
                      {analysis.suggestions.map((suggestion, index) => (
                        <li key={index} className="text-green-400 bg-green-900/20 p-2 rounded">
                          {suggestion}
                        </li>
                      ))}
                    </ul>
                  </div>
                )}

                {analysis.optimizedCode && (
                  <div>
                    <h3 className="text-lg font-semibold mb-2 text-blue-400">Optimized Code</h3>
                    <pre className="bg-gray-700 p-3 rounded text-sm overflow-x-auto">
                      {analysis.optimizedCode}
                    </pre>
                  </div>
                )}
              </div>
            ) : (
              <div className="text-gray-400 text-center py-8">
                {loading ? (
                  <div className="flex items-center justify-center">
                    <FaSpinner className="animate-spin mr-2" />
                    Analyzing your code...
                  </div>
                ) : (
                  'Run analysis to see results'
                )}
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Overlay for mobile when sidebar is open */}
      {isSidebarOpen && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 z-20 lg:hidden"
          onClick={() => setSidebarOpen(false)}
        />
      )}
    </div>
  );
}

export default Debugger;
